var D2lAPI = require('./api.js');

var Cassandra = require('oae-util/lib/cassandra');

module.exports = function(config, callback) {
    _ensureSchema(callback);
};

var _ensureSchema = function(callback) {
    Cassandra.createColumnFamilies({
        'D2LUsers': 'CREATE COLUMNFAMILY D2LUsers (userId text PRIMARY KEY);',
        'D2LSubmissions': 'CREATE COLUMNFAMILY D2LSubmissions (contentId_userId text PRIMARY KEY);'
    }, callback);
};
