var log = require('oae-logger').logger('oae-d2l');
var OAE = require('oae-util/lib/oae');

var D2LAPI = require('./api');

/**
 */
OAE.tenantRouter.on('get', '/api/d2l/isAuthed', function(req, res) {
    D2LAPI.isAuthed(req.ctx, req.header.referer, function(err, isAuthed, authUrl) {
        if (err) {
            return res.send(err);
        }
        return res.send(200, {'isAuthed': isAuthed, 'authUrl': authUrl});
    });
});

OAE.tenantRouter.on('get', '/api/d2l/callback', function(req, res) {
    D2LAPI.cacheUserCreds(req.ctx, req.header.host + req.url, function(err) {
        if (err) {
            return res.send(err);
        }
        return res.redirect(303, req.query.oaeContext);
    });
});

