var log = require('oae-logger').logger('oae-d2l');
var OAE = require('oae-util/lib/oae');

var D2LAPI = require('./api');

/**
 */
OAE.tenantRouter.on('get', '/api/d2l/isAuthed', function(req, res) {
    var redirect = req.query.redirect || req.header.referer;
    D2LAPI.isAuthed(req.ctx, redirect, function(err, isAuthed, authUrl) {
        if (err) {
            return res.send(err.code, err.msg);
        }
        return res.send(200, {'isAuthed': isAuthed, 'authUrl': authUrl});
    });
});

OAE.tenantRouter.on('get', '/api/d2l/callback', function(req, res) {
    D2LAPI.cacheUserCreds(req.ctx, req.header.host + req.url, function(err) {
        if (err) {
            return res.send(err.code, err.msg);
        }
        return res.redirect(303, req.query.oaeContext);
    });
});

OAE.tenantRouter.on('get', '/api/d2l/assessments/:contentId', function(req, res) {
    D2LAPI.getEnrollments(req.ctx, function(err, enrollments) {
        if (err) {
            return res.send(err);
        }
        D2LAPI.getD2LFeedback(req.ctx, req.params.contentId, function(err, d2lStatus) {
            if (err) {
                return res.send(err);
            }
            var assessments = {
                // Some status from D2L
                'status': d2lStatus,
                // All courses this user is in
                'assessors': enrollments
            };
            return res.send(200, assessments);
        });
    });
});

OAE.tenantRouter.on('post', '/api/d2l/assessment', function(req, res) {
    // FIXME Stubbed API
    D2LAPI.submitToD2L(req.ctx, req.body.contentId, req.body.courseId, req.body.assignmentId, req.body.description, function(err){
        if (err) {
            return res.send(err.code, err.msg);
        }
        return res.send(200);
    });
});

OAE.tenantRouter.on('get', '/api/d2l/isAssessmentPermitted', function(req, res) {
    // FIXME Stubbed API
    return res.send(200, {'assessmentsPermitted': true});
});
